package com.leetcode.hard;

import java.util.Arrays;

public class LC1092 {

    public String shortestCommonSupersequence(String str1, String str2) {
        int[][] dp = new int[str1.length() + 1][str2.length() + 1];
        for (int[] row : dp) {
            Arrays.fill(row, -1);
        }
        solve(str1, str2, 0, 0, dp);
        StringBuffer buffer = new StringBuffer();
        int i = str1.length(), j = str2.length();
        while (i >= 0 && j >= 0) {

        }
        return buffer.toString();
    }

    private int solve(String str1, String str2, int idx1, int idx2, int[][] dp) {
        if (idx1 >= str1.length() && idx2 >= str2.length()) {
            return 0;
        }
        if (idx1 >= str1.length()) {
            return str2.length() - idx2;
        }
        if (idx2 >= str2.length()) {
            return str1.length() - idx1;
        }
        if (dp[idx1][idx2] != -1) {
            return dp[idx1][idx2];
        }
        if (str1.charAt(idx1) == str2.charAt(idx2)) {
            int val = solve(str1, str2, idx1 + 1, idx2 + 1, dp);
            return dp[idx1][idx2] = str1.charAt(idx1) + val;
        } else {
            int val1 = 1 + solve(str1, str2, idx1 + 1, idx2, dp);
            int val2 = 1 + solve(str1, str2, idx1, idx2 + 1, dp);
            return dp[idx1][idx2] = Math.min(val1, val2);
        }
    }

    public static void main(String[] args) {
        String str1 =
                "";
        String str2 =
                "";
        LC1092 l = new LC1092();
        System.out.println(l.shortestCommonSupersequence(str1, str2));
    }


}
